<?phpnamespace App\Helpers\FilamentAstrotomic\Resources\Concerns;use App\Helpers\FilamentAstrotomic\FilamentAstrotomicTranslatableContentDriver;use App\Helpers\FilamentAstrotomic\FilamentAstrotomicTranslatablePlugin;use Filament\Support\Contracts\TranslatableContentDriver;trait HasLocales{    use MutateTranslatableData;    /**     * @return class-string<TranslatableContentDriver> | null     */    public function getFilamentTranslatableContentDriver(): ?string    {        return FilamentAstrotomicTranslatableContentDriver::class;    }    public static function getTranslatableLocales(): array    {        /** @var FilamentAstrotomicTranslatablePlugin $plugin */        $plugin = filament('filament-astrotomic-translatable');        return $plugin->allLocales();    }}