<?phpdeclare(strict_types=1);namespace App\Helpers\FilamentAstrotomic\Resources\Pages\Record;use Astrotomic\Translatable\Contracts\Translatable;use App\Helpers\FilamentAstrotomic\Resources\Concerns\HasLocales;use Filament\Resources\Pages\EditRecord;use Illuminate\Database\Eloquent\Model;/** * @mixin EditRecord */trait EditTranslatable{    use HasLocales;    protected function fillForm(): void    {        $this->callHook('beforeFill');        /** @var Model|Translatable $record */        $record = $this->getRecord();        $recordAttributes = $record->attributesToArray();        $recordAttributes = $this->mutateTranslatableData($record, $recordAttributes);        $recordAttributes = $this->mutateFormDataBeforeFill($recordAttributes);        $this->form->fill($recordAttributes);        $this->callHook('afterFill');    }}